import React, { Component } from 'react'

class AddTodo extends Component {
    state={
        id:4,
        title : "",
        complete :false
    }
    update= (e) =>{
        this.setState({
            title:e.target.value
        })
    }
    render() {
        return (
            <form action="">
                <input type="text" className="form-control rounded-0"  placeholder="write your todo" onChange={this.update} value={this.state.value}/>
                <button className="form-control rounded-0 btn-secondary" type="submit"> Add Todo</button>
            </form>
        )
    }
}

export default AddTodo;
